import styled from "styled-components";

export const Container = styled.div`
  position: fixed;
  right: 0;
  top: 0;
  padding: 0;
  overflow: hidden:
  z-index: 9999;s


  @media (max-width: 420px) {
    width: 100%;

  }
`;
